package javaeuler;

public class ProductsNumbers {

	public static void main(String[] args) {
		//Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product?

		String str = "";
		long[] myLong = new long[1000];
		for(int i = 0; i < str.length(); i++) {
			myLong[i] = Long.parseLong(String.valueOf(str.charAt(i)));
		}
		
		int j = calc(myLong, 0, 1000);
		//System.out.println(j);
		long product = (long)(myLong[j]*myLong[j+1]*myLong[j+2]*myLong[j+3]*myLong[j+4]*myLong[j+5]*myLong[j+6]*myLong[j+7]*myLong[j+8]*myLong[j+9]*myLong[j+10]*myLong[j+11]*myLong[j+12]);
		System.out.println(String.valueOf(product));
	}
	
	public static int calc(long[] a, int low, int high) {
		int i = low;
		
		for(int c = low; c < high-12; c++) {
				long product = (long)(a[c] * a[c+1] * a[c+2]*a[c+3]* a[c+4] * a[c+5]*a[c+6]*a[c+7] * a[c+8] * a[c+9]*a[c+10]*a[c+11]*a[c+12]);
				long product2 = (long)(a[i] * a[i+1] * a[i+2] * a[i+3]* a[i+4] * a[i+5] * a[i+6]* a[i+7] * a[i+8] * a[i+9]* a[i+10] * a[i+11] * a[i+12]);
			if (product > product2) {
				i = c;
				//System.out.println(product);
			}
		}
		
		return i;
	}

}
